import React, { useState } from 'react';

const Reservation = ({ space }) => {
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [success, setSuccess] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validación simple de fechas
    if (new Date(startDate) >= new Date(endDate)) {
      alert('La fecha de inicio debe ser anterior a la fecha de fin.');
      return;
    }

    // Simular reserva exitosa
    setSuccess(true);
  };

  return (
    <div className="container mx-auto p-8">
      <h1 className="text-3xl font-bold mb-4">Reservar: {space.name}</h1>

      {success ? (
        <div className="bg-green-100 text-green-800 p-4 rounded-lg">
          ¡Reserva realizada con éxito para {space.name} del {startDate} al {endDate}!
        </div>
      ) : (
        <form onSubmit={handleSubmit} className="bg-gray-50 p-4 rounded-lg shadow-md">
          <div className="mb-4">
            <label htmlFor="startDate" className="block text-gray-700">
              Fecha de Inicio:
            </label>
            <input
              type="date"
              id="startDate"
              value={startDate}
              onChange={(e) => setStartDate(e.target.value)}
              className="border rounded p-2 w-full"
              required
            />
          </div>

          <div className="mb-4">
            <label htmlFor="endDate" className="block text-gray-700">
              Fecha de Fin:
            </label>
            <input
              type="date"
              id="endDate"
              value={endDate}
              onChange={(e) => setEndDate(e.target.value)}
              className="border rounded p-2 w-full"
              required
            />
          </div>

          <button
            type="submit"
            className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 transition"
          >
            Confirmar Reserva
          </button>
        </form>
      )}
    </div>
  );
};

export default Reservation;
