import React, { useState, useEffect } from 'react';
import SpaceCard from '../components/SpaceCard';
import AddSpace from '../pages/AddSpace';
import Favorites from '../pages/Favorites';
import HostDashboard from '../pages/HostDashboard';

const Home = ({ spaces, onFavorite, favorites, currentPage, setCurrentPage }) => {
  const [filteredSpaces, setFilteredSpaces] = useState([]);
  const [search, setSearch] = useState('');
  const [priceRange, setPriceRange] = useState([0, 300]);
  const [selectedType, setSelectedType] = useState('');
  const [sortOrder, setSortOrder] = useState('');
  const [selectedSpace, setSelectedSpace] = useState(null);
  const [loading, setLoading] = useState(true);

  const itemsPerPage = 6;

  // Simula la carga inicial
  useEffect(() => {
    setTimeout(() => {
      setLoading(false);
    }, 500);
  }, []);

  // Filtro dinámico basado en búsqueda, precio y tipo
  useEffect(() => {
    let results = spaces.filter((space) => {
      const matchesSearch = space.name.toLowerCase().includes(search.toLowerCase());
      const matchesPrice = space.price >= priceRange[0] && space.price <= priceRange[1];
      const matchesType = selectedType ? space.type === selectedType : true;

      return matchesSearch && matchesPrice && matchesType;
    });

    // Ordena los resultados según el estado
    if (sortOrder === 'price-asc') {
      results.sort((a, b) => a.price - b.price);
    } else if (sortOrder === 'price-desc') {
      results.sort((a, b) => b.price - a.price);
    } else if (sortOrder === 'name-asc') {
      results.sort((a, b) => a.name.localeCompare(b.name));
    }

    setFilteredSpaces(results);
    setCurrentPage(1); // Reinicia la paginación al aplicar filtros
  }, [spaces, search, priceRange, selectedType, sortOrder, setCurrentPage]);

  // Espacios de la página actual
  const paginatedSpaces = filteredSpaces.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  // Abre el modal de detalles rápidos
  const openModal = (space) => setSelectedSpace(space);

  // Cierra el modal
  const closeModal = () => setSelectedSpace(null);

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <p className="text-2xl font-bold text-gray-600">Cargando espacios...</p>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-8">
      <h1 className="text-4xl font-bold mb-6 text-center text-gray-700">
        Encuentra tu espacio ideal
      </h1>

      {/* Filtros */}
      <div className="flex flex-col sm:flex-row justify-between items-center mb-6 bg-gray-50 p-4 rounded-lg shadow-sm">
        {/* Búsqueda */}
        <input
          type="text"
          placeholder="Buscar por ubicación o nombre"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="border rounded p-2 w-full max-w-xs mb-4 sm:mb-0"
        />

        {/* Tipo de Espacio */}
        <select
          value={selectedType}
          onChange={(e) => setSelectedType(e.target.value)}
          className="border rounded p-2"
        >
          <option value="">Todos los tipos</option>
          <option value="garage">Garage</option>
          <option value="almacen">Almacén</option>
          <option value="oficina">Oficina</option>
        </select>

        {/* Rango de Precios */}
        <div className="flex items-center gap-4">
          <span className="text-gray-600">Rango de precios:</span>
          <input
            type="range"
            min="0"
            max="300"
            value={priceRange[1]}
            onChange={(e) => setPriceRange([0, Number(e.target.value)])}
            className="w-40"
          />
          <span className="text-gray-700 font-bold">${priceRange[1]}</span>
        </div>

        {/* Ordenar */}
        <select
          value={sortOrder}
          onChange={(e) => setSortOrder(e.target.value)}
          className="border rounded p-2"
        >
          <option value="">Ordenar por</option>
          <option value="price-asc">Precio: Menor a Mayor</option>
          <option value="price-desc">Precio: Mayor a Menor</option>
          <option value="name-asc">Nombre: A-Z</option>
        </select>
      </div>

      {/* Lista de Espacios */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {paginatedSpaces.length > 0 ? (
          paginatedSpaces.map((space) => (
            <SpaceCard
              key={space.id}
              space={space}
              onFavorite={onFavorite}
              isFavorite={favorites.includes(space.id)}
              openModal={openModal} // Pasar función de modal
            />
          ))
        ) : (
          <p className="col-span-full text-center text-gray-500">
            No se encontraron espacios.
          </p>
        )}
      </div>

      {/* Paginación */}
      <div className="flex justify-center mt-6">
        {Array.from({ length: Math.ceil(filteredSpaces.length / itemsPerPage) }, (_, index) => (
          <button
            key={index}
            onClick={() => setCurrentPage(index + 1)}
            className={`mx-1 px-4 py-2 rounded ${
              currentPage === index + 1 ? 'bg-blue-500 text-white' : 'bg-gray-200'
            }`}
          >
            {index + 1}
          </button>
        ))}
      </div>

      {/* Modal de Detalles Rápidos */}
      {selectedSpace && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-lg max-w-md w-full">
            <h2 className="text-2xl font-bold mb-4">{selectedSpace.name}</h2>
            <p>{selectedSpace.description}</p>
            <p className="text-blue-500 font-bold mt-4">${selectedSpace.price}/mes</p>
            <button
              onClick={closeModal}
              className="mt-6 bg-red-500 text-white py-2 px-4 rounded hover:bg-red-600"
            >
              Cerrar
            </button>
          </div>
        </div>
      )}

      {/* Componentes adicionales */}
      <div className="mt-10">
        <AddSpace />
        <Favorites />
        <HostDashboard />
      </div>
    </div>
  );
};

export default Home;
