import React from 'react';
import { Heart, MapPin } from 'lucide-react';
import { Link } from 'react-router-dom';

const SpaceCard = ({ space, onFavorite, isFavorite, openModal }) => {
  return (
    <div className="bg-white rounded-lg shadow hover:shadow-lg transition-all cursor-pointer">
      {/* Imagen del espacio */}
      <div
        className="h-48 bg-gray-200 rounded-t-lg flex items-center justify-center text-gray-500"
        style={{
          backgroundImage: `url(${space.image || 'https://via.placeholder.com/400x300'})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
        }}
      >
        {!space.image && <span>400 x 300</span>}
      </div>

      {/* Detalles del espacio */}
      <div className="p-4">
        <div className="flex justify-between items-start mb-2">
          <div>
            <h3 className="font-bold text-lg">{space.name}</h3>
            <div className="flex items-center gap-1 text-gray-600 text-sm">
              <MapPin className="w-4 h-4" />
              <span>{space.location}</span>
            </div>
          </div>

          {/* Botón de Favorito */}
          <button onClick={() => onFavorite(space.id)} aria-label="Marcar como favorito">
            <Heart
              className={`w-6 h-6 ${
                isFavorite ? 'text-red-500 fill-current' : 'text-gray-400'
              }`}
            />
          </button>
        </div>

        <p className="text-blue-500 font-bold">${space.price}/mes</p>

        {/* Botón para abrir el modal */}
        <button
          onClick={() => openModal(space)}
          className="mt-4 bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 transition"
        >
          Ver Detalles
        </button>

        {/* Botón para reservar */}
        <Link
          to={`/space/${space.id}/reserve`}
          className="mt-2 inline-block bg-green-500 text-white py-2 px-4 rounded hover:bg-green-600 transition"
        >
          Reservar Ahora
        </Link>
      </div>
    </div>
  );
};

export default SpaceCard;
