import React, { useState } from 'react';

const AddSpace = ({ user }) => {
  const [name, setName] = useState('');
  const [location, setLocation] = useState('');
  const [price, setPrice] = useState('');
  const [description, setDescription] = useState('');
  const [success, setSuccess] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Simular envío de datos a un backend
    const newSpace = {
      id: Date.now(),
      name,
      location,
      price: parseFloat(price),
      description,
      owner: user.email, // Añade el usuario autenticado como propietario
    };

    console.log('Nuevo Espacio:', newSpace);
    setSuccess(true);

    // Limpiar formulario
    setName('');
    setLocation('');
    setPrice('');
    setDescription('');
  };

  return (
    <div className="container mx-auto p-8">
      <h1 className="text-3xl font-bold mb-4 text-center">Agregar Nuevo Espacio</h1>

      {success && (
        <div className="bg-green-100 text-green-800 p-4 rounded-lg mb-4">
          ¡Espacio agregado con éxito!
        </div>
      )}

      <form
        onSubmit={handleSubmit}
        className="bg-gray-50 p-6 rounded-lg shadow-lg max-w-md mx-auto"
      >
        <div className="mb-4">
          <label htmlFor="name" className="block text-gray-700 font-bold mb-2">
            Nombre del Espacio:
          </label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="border rounded p-2 w-full"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="location" className="block text-gray-700 font-bold mb-2">
            Ubicación:
          </label>
          <input
            type="text"
            id="location"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            className="border rounded p-2 w-full"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="price" className="block text-gray-700 font-bold mb-2">
            Precio Mensual:
          </label>
          <input
            type="number"
            id="price"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            className="border rounded p-2 w-full"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="description" className="block text-gray-700 font-bold mb-2">
            Descripción:
          </label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="border rounded p-2 w-full"
            rows="4"
            required
          ></textarea>
        </div>

        <button
          type="submit"
          className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 transition"
        >
          Agregar Espacio
        </button>
      </form>
    </div>
  );
};

export default AddSpace;
