import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const SpaceDetails = () => {
  const { id } = useParams();
  const [space, setSpace] = useState(null);
  const [loading, setLoading] = useState(true);
  const [comments, setComments] = useState([]); // Lista de comentarios
  const [newComment, setNewComment] = useState(''); // Nuevo comentario

  useEffect(() => {
    // Fetch del espacio
    fetch(`http://localhost:5000/spaces/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setSpace(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error(error);
        setLoading(false);
      });

    // Fetch de comentarios
    fetch(`http://localhost:5000/comments?spaceId=${id}`)
      .then((res) => res.json())
      .then((data) => setComments(data));
  }, [id]);

  const handleAddComment = () => {
    if (!newComment.trim()) return;

    const comment = {
      id: Date.now(),
      spaceId: id,
      text: newComment,
      createdAt: new Date().toISOString(),
    };

    // Simula agregar un comentario (JSON Server)
    fetch('http://localhost:5000/comments', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(comment),
    }).then(() => {
      setComments((prev) => [...prev, comment]);
      setNewComment('');
    });
  };

  if (loading) {
    return <p className="text-center mt-8">Cargando espacio...</p>;
  }

  if (!space) {
    return <p className="text-center mt-8 text-red-500">No se encontró el espacio.</p>;
  }

  return (
    <div className="container mx-auto p-8">
      <img
        src={space.image}
        alt={space.name}
        className="w-full h-64 object-cover rounded-lg mb-6"
      />
      <h1 className="text-3xl font-bold mb-4">{space.name}</h1>
      <p className="text-gray-700 mb-4">{space.description}</p>
      <p className="text-gray-500">Ubicación: {space.location}</p>
      <p className="text-blue-500 font-bold">{space.price}/mes</p>

      {/* Sección de comentarios */}
      <div className="mt-8">
        <h2 className="text-2xl font-bold mb-4">Comentarios</h2>
        <ul className="mb-4">
          {comments.length > 0 ? (
            comments.map((comment) => (
              <li key={comment.id} className="mb-2 border-b pb-2">
                <p className="text-gray-700">{comment.text}</p>
                <p className="text-sm text-gray-500">
                  {new Date(comment.createdAt).toLocaleString()}
                </p>
              </li>
            ))
          ) : (
            <p className="text-gray-500">No hay comentarios aún.</p>
          )}
        </ul>
        <div className="flex items-center gap-4">
          <input
            type="text"
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
            placeholder="Escribe un comentario..."
            className="border rounded p-2 flex-grow"
          />
          <button
            onClick={handleAddComment}
            className="bg-blue-500 text-white p-2 rounded"
          >
            Enviar
          </button>
        </div>
      </div>
    </div>
  );
};

export default SpaceDetails;
