import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import Home from './pages/Home';
import SpaceDetails from './pages/SpaceDetails';
import AddSpace from './pages/AddSpace';
import Favorites from './pages/Favorites';
import HostDashboard from './pages/HostDashboard';
import Reservation from './pages/Reservation';
import Login from './pages/Login';
import './index.css';

function App() {
  // Estados globales
  const [spaces, setSpaces] = useState([]); // Lista de espacios
  const [favorites, setFavorites] = useState(() => {
    const savedFavorites = localStorage.getItem('favorites');
    return savedFavorites ? JSON.parse(savedFavorites) : [];
  });
  const [user, setUser] = useState(null); // Usuario autenticado

  // Fetch inicial de espacios
  useEffect(() => {
    fetch('http://localhost:5000/spaces')
      .then((res) => res.json())
      .then((data) => setSpaces(data))
      .catch((err) => console.error('Error al cargar espacios:', err));
  }, []);

  // Guardar favoritos en localStorage
  useEffect(() => {
    localStorage.setItem('favorites', JSON.stringify(favorites));
  }, [favorites]);

  // Manejar favoritos
  const handleFavorite = (id) => {
    setFavorites((prev) =>
      prev.includes(id) ? prev.filter((fav) => fav !== id) : [...prev, id]
    );
  };

  // Agregar un nuevo espacio
  const handleAddSpace = (newSpace) => {
    setSpaces((prev) => [...prev, newSpace]);
  };

  return (
    <Router>
      <div className="flex flex-col min-h-screen">
        {/* Navbar */}
        <Navbar user={user} setUser={setUser} />

        {/* Contenido principal */}
        <div className="flex-grow">
          <Routes>
            {/* PÃ¡gina de Inicio */}
            <Route
              path="/"
              element={
                <Home
                  spaces={spaces}
                  onFavorite={handleFavorite}
                  favorites={favorites}
                />
              }
            />

            {/* Detalles del Espacio */}
            <Route path="/space/:id" element={<SpaceDetails />} />

            {/* Reservar Espacio */}
            <Route path="/space/:id/reserve" element={<Reservation />} />

            {/* AÃ±adir Espacio */}
            <Route
              path="/add-space"
              element={
                user ? (
                  <AddSpace user={user} onAddSpace={handleAddSpace} />
                ) : (
                  <Login setUser={setUser} />
                )
              }
            />

            {/* Espacios Favoritos */}
            <Route
              path="/favorites"
              element={
                <Favorites
                  spaces={spaces}
                  favorites={favorites}
                  onFavorite={handleFavorite}
                />
              }
            />

            {/* Dashboard del AnfitriÃ³n */}
            <Route
              path="/host-dashboard"
              element={
                user ? (
                  <HostDashboard spaces={spaces} />
                ) : (
                  <Login setUser={setUser} />
                )
              }
            />

            {/* Login */}
            <Route path="/login" element={<Login setUser={setUser} />} />
          </Routes>
        </div>

        {/* Footer */}
        <Footer />
      </div>
    </Router>
  );
}

export default App;
