import React, { useState } from 'react';
import { Heart, HeartFill } from 'lucide-react';

const SpaceCard = ({ space, onFavorite, isFavorite, openModal }) => {
  const [hovered, setHovered] = useState(false);

  const handleFavorite = () => {
    onFavorite(space.id);
  };

  return (
    <div
      className="border rounded-lg overflow-hidden shadow-md hover:shadow-lg transition-shadow duration-200"
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
    >
      <img
        src={space.image}
        alt={space.name}
        className="w-full h-48 object-cover"
      />

      <div className="p-4">
        <h3 className="text-lg font-bold text-gray-800 mb-2">{space.name}</h3>
        <p className="text-gray-600 text-sm mb-4">{space.description}</p>

        <div className="flex items-center justify-between">
          <span className="text-blue-500 font-bold">${space.price}/mes</span>
          <button onClick={handleFavorite} className="text-gray-500 hover:text-red-500">
            {isFavorite ? <HeartFill className="w-6 h-6" /> : <Heart className="w-6 h-6" />}
          </button>
        </div>

        <button
          onClick={() => openModal(space)}
          className="mt-4 w-full bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 transition-colors"
        >
          Ver detalles
        </button>
      </div>
    </div>
  );
};

export default SpaceCard;
