import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const Reservation = ({ spaces, reservations, onReserve }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const space = spaces.find((space) => space.id === parseInt(id, 10));
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const isDateConflict = (start, end) => {
    return reservations.some(
      (reservation) =>
        reservation.spaceId === space.id &&
        ((new Date(start) >= new Date(reservation.startDate) &&
          new Date(start) <= new Date(reservation.endDate)) ||
          (new Date(end) >= new Date(reservation.startDate) &&
            new Date(end) <= new Date(reservation.endDate)))
    );
  };

  const handleReservation = (e) => {
    e.preventDefault();

    if (!startDate || !endDate) {
      setErrorMessage('Por favor selecciona ambas fechas.');
      return;
    }

    if (new Date(startDate) > new Date(endDate)) {
      setErrorMessage('La fecha de inicio no puede ser posterior a la fecha de fin.');
      return;
    }

    if (isDateConflict(startDate, endDate)) {
      setErrorMessage(
        'Ya existe una reserva en este rango de fechas. Por favor selecciona fechas diferentes.'
      );
      return;
    }

    onReserve(space.id, startDate, endDate);
    setSuccessMessage(`¡Reserva realizada exitosamente para ${space.name}!`);
    setErrorMessage('');
    setStartDate('');
    setEndDate('');
  };

  if (!space) {
    return <p className="text-center text-red-500 mt-8">Espacio no encontrado.</p>;
  }

  return (
    <div className="container mx-auto p-8">
      <h1 className="text-4xl font-bold mb-6 text-center text-gray-800">
        Reservar Espacio: {space.name}
      </h1>
      <form
        onSubmit={handleReservation}
        className="bg-gray-50 p-6 rounded-lg shadow-lg max-w-md mx-auto"
      >
        {successMessage && (
          <div className="bg-green-100 text-green-800 p-4 rounded-lg mb-4">
            {successMessage}
          </div>
        )}
        {errorMessage && (
          <div className="bg-red-100 text-red-800 p-4 rounded-lg mb-4">
            {errorMessage}
          </div>
        )}
        <div className="mb-4">
          <label htmlFor="startDate" className="block text-gray-700 font-bold mb-2">
            Fecha de Inicio:
          </label>
          <input
            type="date"
            id="startDate"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
            className="border rounded p-2 w-full"
            required
          />
        </div>
        <div className="mb-4">
          <label htmlFor="endDate" className="block text-gray-700 font-bold mb-2">
            Fecha de Fin:
          </label>
          <input
            type="date"
            id="endDate"
            value={endDate}
            onChange={(e) => setEndDate(e.target.value)}
            className="border rounded p-2 w-full"
            required
          />
        </div>
        <button
          type="submit"
          className="bg-orange-500 text-white py-2 px-4 rounded hover:bg-orange-600 transition"
        >
          Reservar
        </button>
        <button
          type="button"
          onClick={() => navigate(-1)}
          className="ml-4 bg-gray-500 text-white py-2 px-4 rounded hover:bg-gray-600 transition"
        >
          Volver
        </button>
      </form>
    </div>
  );
};

export default Reservation;
