import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';

const Reservation = () => {
  const location = useLocation();
  const { space } = location.state || {};

  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleReservation = (e) => {
    e.preventDefault();

    // Validar fechas
    if (!startDate || !endDate) {
      setErrorMessage('Por favor selecciona ambas fechas.');
      return;
    }
    if (new Date(startDate) > new Date(endDate)) {
      setErrorMessage('La fecha de inicio no puede ser posterior a la fecha de fin.');
      return;
    }

    // Simular una reserva exitosa
    setSuccessMessage(`¡Reserva realizada para ${space.name}!`);
    setErrorMessage('');
    setStartDate('');
    setEndDate('');
  };

  if (!space) {
    return (
      <div className="container mx-auto p-8">
        <p className="text-center text-red-500 text-xl">Espacio no encontrado.</p>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-8">
      <h1 className="text-3xl font-bold mb-6 text-center text-gray-700">
        Reservar Espacio: {space.name}
      </h1>
      <form
        onSubmit={handleReservation}
        className="bg-gray-50 p-6 rounded-lg shadow-lg max-w-md mx-auto"
      >
        {successMessage && (
          <div className="bg-green-100 text-green-800 p-4 rounded-lg mb-4">
            {successMessage}
          </div>
        )}
        {errorMessage && (
          <div className="bg-red-100 text-red-800 p-4 rounded-lg mb-4">
            {errorMessage}
          </div>
        )}
        <div className="mb-4">
          <label htmlFor="startDate" className="block text-gray-700 font-bold mb-2">
            Fecha de Inicio:
          </label>
          <input
            type="date"
            id="startDate"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
            className="border rounded p-2 w-full"
            required
          />
        </div>
        <div className="mb-4">
          <label htmlFor="endDate" className="block text-gray-700 font-bold mb-2">
            Fecha de Fin:
          </label>
          <input
            type="date"
            id="endDate"
            value={endDate}
            onChange={(e) => setEndDate(e.target.value)}
            className="border rounded p-2 w-full"
            required
          />
        </div>
        <button
          type="submit"
          className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 transition"
        >
          Reservar
        </button>
      </form>
    </div>
  );
};

export default Reservation;
