import React from 'react';
import { NavLink } from 'react-router-dom';

const Navbar = ({ user }) => {
  return (
    <nav className="bg-blue-600 text-white shadow-lg sticky top-0 z-50">
      <div className="container mx-auto flex justify-between items-center p-4">
        {/* Logo */}
        <NavLink to="/" className="text-2xl font-bold flex items-center">
          🚗 GarageShare
        </NavLink>

        {/* Links */}
        <div className="flex space-x-6">
          <NavLink
            to="/"
            className={({ isActive }) =>
              `hover:text-gray-200 ${isActive ? 'text-yellow-300' : ''}`
            }
          >
            Inicio
          </NavLink>

          {/* Mostrar Favoritos y Dashboard solo si el usuario ha iniciado sesión */}
          {user && (
            <>
              <NavLink
                to="/favorites"
                className={({ isActive }) =>
                  `hover:text-gray-200 ${isActive ? 'text-yellow-300' : ''}`
                }
              >
                Favoritos
              </NavLink>
              <NavLink
                to="/host-dashboard"
                className={({ isActive }) =>
                  `hover:text-gray-200 ${isActive ? 'text-yellow-300' : ''}`
                }
              >
                Dashboard
              </NavLink>
            </>
          )}

          <NavLink
            to="/add-space"
            className={({ isActive }) =>
              `hover:text-gray-200 ${isActive ? 'text-yellow-300' : ''}`
            }
          >
            Agregar Espacio
          </NavLink>

          {/* Botón para iniciar o cerrar sesión */}
          {user ? (
            <button
              onClick={() => window.location.reload()}
              className="hover:text-gray-200"
            >
              Cerrar Sesión
            </button>
          ) : (
            <NavLink
              to="/login"
              className={({ isActive }) =>
                `hover:text-gray-200 ${isActive ? 'text-yellow-300' : ''}`
              }
            >
              Iniciar Sesión
            </NavLink>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
