import React from 'react';
import { NavLink } from 'react-router-dom';

const Navbar = ({ user, setUser }) => {
  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('user'); // AsegÃºrate de manejar el almacenamiento local
  };

  return (
    <nav className="bg-blue-600 text-white shadow-lg sticky top-0 z-50">
      <div className="container mx-auto flex justify-between items-center p-4">
        {/* Logo */}
        <NavLink to="/" className="text-2xl font-bold flex items-center">
          ðŸš— GarageShare
        </NavLink>

        {/* Links */}
        <div className="flex space-x-6">
          <NavLink
            to="/"
            className={({ isActive }) =>
              `hover:text-gray-200 ${isActive ? 'text-yellow-300' : ''}`
            }
          >
            Inicio
          </NavLink>
          {user && (
            <>
              <NavLink
                to="/favorites"
                className={({ isActive }) =>
                  `hover:text-gray-200 ${isActive ? 'text-yellow-300' : ''}`
                }
              >
                Favoritos
              </NavLink>
              <NavLink
                to="/user-dashboard"
                className={({ isActive }) =>
                  `hover:text-gray-200 ${isActive ? 'text-yellow-300' : ''}`
                }
              >
                Mi Panel
              </NavLink>
            </>
          )}
          {user?.role?.includes('host') && (
            <NavLink
              to="/host-dashboard"
              className={({ isActive }) =>
                `hover:text-gray-200 ${isActive ? 'text-yellow-300' : ''}`
              }
            >
              Dashboard de Host
            </NavLink>
          )}
          {user?.role?.includes('host') && (
            <NavLink
              to="/add-space"
              className={({ isActive }) =>
                `hover:text-gray-200 ${isActive ? 'text-yellow-300' : ''}`
              }
            >
              Agregar Espacio
            </NavLink>
          )}
          {user ? (
            <button
              onClick={handleLogout}
              className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
            >
              Cerrar SesiÃ³n
            </button>
          ) : (
            <NavLink
              to="/login"
              className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
            >
              Iniciar SesiÃ³n
            </NavLink>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
