import React, { useState } from 'react';

const SpaceDetails = ({ space }) => {
  const [dates, setDates] = useState({ start: '', end: '' });

  const handleReserve = () => {
    alert(`Reserva completada para ${space.name} del ${dates.start} al ${dates.end}`);
  };

  return (
    <div className="container mx-auto p-8">
      <img src={space.image} alt={space.name} className="w-full h-64 object-cover rounded-lg mb-6" />
      <h1 className="text-3xl font-bold mb-4">{space.name}</h1>
      <p className="text-gray-700 mb-4">{space.description}</p>
      <div className="bg-gray-50 p-4 rounded shadow-md">
        <h2 className="font-bold mb-2">Reserva este espacio</h2>
        <div className="flex gap-4">
          <input
            type="date"
            value={dates.start}
            onChange={(e) => setDates({ ...dates, start: e.target.value })}
            className="border rounded p-2"
          />
          <input
            type="date"
            value={dates.end}
            onChange={(e) => setDates({ ...dates, end: e.target.value })}
            className="border rounded p-2"
          />
        </div>
        <button
          onClick={handleReserve}
          className="bg-blue-500 text-white p-2 mt-4 rounded"
        >
          Reservar
        </button>
      </div>
    </div>
  );
};

export default SpaceDetails;
