import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import Home from './pages/Home';
import SpaceDetails from './pages/SpaceDetails';
import AddSpace from './pages/AddSpace';
import Favorites from './pages/Favorites';
import HostDashboard from './pages/HostDashboard';
import Reservation from './pages/Reservation';
import Login from './pages/Login';
import './index.css';

function App() {
  const [spaces, setSpaces] = useState([]); // Espacios disponibles
  const [favorites, setFavorites] = useState(() => {
    // Cargar favoritos desde el localStorage
    const savedFavorites = localStorage.getItem('favorites');
    return savedFavorites ? JSON.parse(savedFavorites) : [];
  });
  const [user, setUser] = useState(null); // Estado del usuario autenticado

  // Fetch inicial de espacios desde una API simulada
  useEffect(() => {
    fetch('http://localhost:5000/spaces')
      .then((res) => res.json())
      .then((data) => setSpaces(data))
      .catch((err) => console.error('Error al cargar espacios:', err));
  }, []);

  // Guardar favoritos en el localStorage
  useEffect(() => {
    localStorage.setItem('favorites', JSON.stringify(favorites));
  }, [favorites]);

  // Manejar la adici贸n y eliminaci贸n de favoritos
  const handleFavorite = (id) => {
    setFavorites((prev) =>
      prev.includes(id) ? prev.filter((fav) => fav !== id) : [...prev, id]
    );
  };

  return (
    <Router>
      <div className="flex flex-col min-h-screen">
        {/* Navbar */}
        <Navbar user={user} />

        {/* Contenido Principal */}
        <div className="flex-grow">
          <Routes>
            <Route
              path="/"
              element={
                <Home
                  spaces={spaces}
                  onFavorite={handleFavorite}
                  favorites={favorites}
                />
              }
            />
            <Route path="/space/:id" element={<SpaceDetails />} />
            <Route path="/space/:id/reserve" element={<Reservation />} />
            <Route
              path="/add-space"
              element={user ? <AddSpace /> : <Login setUser={setUser} />}
            />
            <Route
              path="/favorites"
              element={
                <Favorites
                  spaces={spaces}
                  favorites={favorites}
                  onFavorite={handleFavorite}
                />
              }
            />
            <Route
              path="/host-dashboard"
              element={user ? <HostDashboard /> : <Login setUser={setUser} />}
            />
            <Route path="/login" element={<Login setUser={setUser} />} />
          </Routes>
        </div>

        {/* Footer */}
        <Footer />
      </div>
    </Router>
  );
}

export default App;
