import React from 'react';
import { Star } from 'lucide-react';

const SpaceCard = ({ space, onFavorite, isFavorite, openModal }) => {
  const averageRating = space.reviews?.length
    ? space.reviews.reduce((sum, review) => sum + review.rating, 0) / space.reviews.length
    : 0;

  return (
    <div className="border rounded-lg shadow-sm overflow-hidden bg-white">
      <img src={space.image || '/placeholder.png'} alt={space.name} className="w-full h-40 object-cover" />
      <div className="p-4">
        <h2 className="text-lg font-bold">{space.name}</h2>
        <p className="text-sm text-gray-600">{space.location}</p>
        <p className="text-blue-500 font-bold mt-2">${space.price}/mes</p>
        {/* Estrellas */}
        <div className="flex items-center mt-2">
          {[...Array(5)].map((_, index) => (
            <Star
              key={index}
              size={16}
              color={index < averageRating ? 'gold' : 'gray'}
              fill={index < averageRating ? 'gold' : 'none'}
            />
          ))}
          <span className="ml-2 text-sm text-gray-500">({space.reviews?.length || 0})</span>
        </div>
        <div className="flex justify-between items-center mt-4">
          <button
            className="text-blue-500 text-sm underline"
            onClick={() => openModal(space)}
          >
            Ver detalles
          </button>
          <button
            className={`text-sm ${isFavorite ? 'text-red-500' : 'text-gray-500'}`}
            onClick={() => onFavorite(space.id)}
          >
            {isFavorite ? '‚ù§Ô∏è Favorito' : 'ü§ç A√±adir'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default SpaceCard;
