import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import Home from './pages/Home';
import UserDashboard from './pages/UserDashboard';
import Reservation from './pages/Reservation';
import './index.css';

function App() {
  const initialSpaces = [
    // Initial mock data for spaces
    { id: 1, name: 'Garage Grande', price: 150, type: 'garage' },
    { id: 2, name: 'Almacén Pequeño', price: 80, type: 'almacen' },
  ];

  const [spaces, setSpaces] = useState(initialSpaces);
  const [reservations, setReservations] = useState([]);
  const [user, setUser] = useState(null); // Simulated user data

  useEffect(() => {
    // Simulated fetching user info and reservations
    setUser({ id: 1, name: 'John Doe' });
    setReservations([
      {
        id: 1,
        userId: 1,
        spaceName: 'Garage Grande',
        startDate: '2023-12-01',
        endDate: '2023-12-10',
        totalPrice: 300,
      },
    ]);
  }, []);

  const handleReserve = (spaceId, startDate, endDate) => {
    const reservedSpace = spaces.find((space) => space.id === spaceId);
    const totalPrice =
      reservedSpace.price *
      Math.ceil(
        (new Date(endDate) - new Date(startDate)) / (1000 * 60 * 60 * 24)
      );

    const newReservation = {
      id: reservations.length + 1,
      userId: user.id,
      spaceName: reservedSpace.name,
      startDate,
      endDate,
      totalPrice,
    };

    setReservations((prev) => [...prev, newReservation]);
  };

  return (
    <Router>
      <div className="flex flex-col min-h-screen">
        <Navbar user={user} setUser={setUser} />
        <div className="flex-grow">
          <Routes>
            <Route
              path="/"
              element={<Home spaces={spaces} onReserve={handleReserve} />}
            />
            <Route
              path="/user-dashboard"
              element={
                user ? (
                  <UserDashboard
                    userId={user.id}
                    reservations={reservations}
                  />
                ) : (
                  <p>Please log in to access your dashboard.</p>
                )
              }
            />
            <Route
              path="/space/:id/reserve"
              element={<Reservation spaces={spaces} />}
            />
          </Routes>
        </div>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
