import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';

const SpaceCard = ({ space, onFavorite }) => {
  const [isFavorite, setIsFavorite] = useState(false);
  const navigate = useNavigate();

  const toggleFavorite = () => {
    setIsFavorite(!isFavorite);
    onFavorite(space.id);
  };

  return (
    <motion.div
      whileHover={{ scale: 1.05 }}
      className="border rounded-lg shadow-md cursor-pointer hover:shadow-lg transition overflow-hidden relative"
    >
      <img
        src={space.image}
        alt={space.name}
        className="w-full h-48 object-cover"
        onClick={() => navigate(`/space/${space.id}`)}
      />
      <button
        onClick={toggleFavorite}
        className={`absolute top-2 right-2 text-white bg-${isFavorite ? 'red' : 'gray'}-500 p-2 rounded-full`}
      >
        {isFavorite ? '‚ù§Ô∏è' : 'ü§ç'}
      </button>
      <div className="p-4">
        <h2 className="font-bold text-lg">{space.name}</h2>
        <p className="text-gray-500">{space.location}</p>
        <p className="text-blue-500 font-bold">{space.price}/mes</p>
      </div>
    </motion.div>
  );
};

export default SpaceCard;
