import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const SpaceDetails = () => {
  const { id } = useParams(); // Obtén el ID del espacio desde la URL
  const [space, setSpace] = useState(null); // Estado para el espacio seleccionado
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch del espacio desde la API
    fetch(`http://localhost:5000/spaces/${id}`)
      .then((res) => {
        if (!res.ok) {
          throw new Error('Error al cargar el espacio');
        }
        return res.json();
      })
      .then((data) => {
        setSpace(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error(error);
        setLoading(false);
      });
  }, [id]);

  if (loading) {
    return <p className="text-center mt-8">Cargando espacio...</p>;
  }

  if (!space) {
    return <p className="text-center mt-8 text-red-500">No se encontró el espacio.</p>;
  }

  return (
    <div className="container mx-auto p-8">
      <img
        src={space.image}
        alt={space.name}
        className="w-full h-64 object-cover rounded-lg mb-6"
      />
      <h1 className="text-3xl font-bold mb-4">{space.name}</h1>
      <p className="text-gray-700 mb-4">{space.description}</p>
      <p className="text-gray-500">Ubicación: {space.location}</p>
      <p className="text-blue-500 font-bold">{space.price}/mes</p>
    </div>
  );
};

export default SpaceDetails;
