import React, { useState } from 'react';

const AddSpace = () => {
  const [space, setSpace] = useState({
    name: '',
    location: '',
    price: '',
    image: '',
    description: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setSpace({ ...space, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch('http://localhost:5000/spaces', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ ...space, id: Date.now() }),
    }).then(() => {
      alert('Espacio agregado exitosamente');
      setSpace({
        name: '',
        location: '',
        price: '',
        image: '',
        description: '',
      });
    });
  };

  return (
    <div className="container mx-auto p-8">
      <h1 className="text-3xl font-bold mb-6">Agregar Nuevo Espacio</h1>
      <form onSubmit={handleSubmit} className="space-y-4 max-w-md mx-auto">
        <input
          type="text"
          name="name"
          value={space.name}
          onChange={handleChange}
          placeholder="Nombre del Espacio"
          className="border rounded p-2 w-full"
          required
        />
        <input
          type="text"
          name="location"
          value={space.location}
          onChange={handleChange}
          placeholder="Ubicación"
          className="border rounded p-2 w-full"
          required
        />
        <input
          type="number"
          name="price"
          value={space.price}
          onChange={handleChange}
          placeholder="Precio"
          className="border rounded p-2 w-full"
          required
        />
        <input
          type="url"
          name="image"
          value={space.image}
          onChange={handleChange}
          placeholder="URL de la Imagen"
          className="border rounded p-2 w-full"
          required
        />
        <textarea
          name="description"
          value={space.description}
          onChange={handleChange}
          placeholder="Descripción"
          className="border rounded p-2 w-full"
          required
        ></textarea>
        <button
          type="submit"
          className="bg-blue-500 text-white p-2 rounded w-full"
        >
          Agregar Espacio
        </button>
      </form>
    </div>
  );
};

export default AddSpace;
